// Login.js
import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from './context/UserContext';
import './login.css';
import saveUserData from './local-data/UserSet';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons'

const Login = () => {
  const [userType, setUserType] = useState('doctor');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();
  const show_icon = <FontAwesomeIcon icon={faEye} />;
  const hide_icon = <FontAwesomeIcon icon={faEyeSlash} />;

  // Set the value of username
  const { setUserId: setContextUserId } = useContext(UserContext);

  const handleUserTypeChange = (event) => {
    setUserType(event.target.value);
  };

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setContextUserId(username);
    const user = {
      name: 'jha',
      id: username,
      userType,
    };
    saveUserData(user);

    // Clear the input fields after submission
    setUsername('');
    setPassword('');

    // Redirect to the profile page
    navigate('/profile');
  };

  const handleSignup = () => {
    navigate('/signup');
  };

  return (
    <div className="container">
      <div className="login-form">
        <h2>Login</h2>
        <div className="user-type-container">
          <label htmlFor="userType">User Type:</label>
          <select
            id="userType"
            name="userType"
            value={userType}
            onChange={handleUserTypeChange}
          >
            <option value="doctor">Doctor</option>
            <option value="staff">Staff</option>
            <option value="staff-relative">Staff Relative</option>
            <option value="student">Student</option>
          </select>
        </div>
        <div className="username-container">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={handleUsernameChange}
            required
          />
        </div>
        <div className="password-container">
          <label htmlFor="password">Password:</label>
          <div className="password-input-container">
            <input
              type={showPassword ? 'text' : 'password'}
              id="password"
              name="password"
              value={password}
              onChange={handlePasswordChange}
            />
            <button
              type="button"
              className="password-toggle-button"
              onClick={togglePasswordVisibility}
            >
              {showPassword ? show_icon : hide_icon}
            </button>
          </div>
        </div>
        <div className="button-container">
          <input type="submit" value="Login" />
          <button type="button" onClick={handleSignup}>
            Signup
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
