import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from './context/UserContext';
import './login.css';
import saveUserData from './local-data/UserSet';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  // Set the value of username
  const { setUserId: setContextUserId } = useContext(UserContext);

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setContextUserId(username);
    const user = {
      name: 'jha',
      id: username,
    };
    saveUserData(user);

    // Clear the input fields after submission
    setUsername('');
    setPassword('');

    // Redirect to the profile page
    navigate('/profile');
  };

  const handleSignup = () => {
    navigate('/signup');
  };

  return (
    <div className="container p-5">
      <img src='https://www.svnit.ac.in/conference/frsm2023/hit/svnit_logo.png' alt="logo"
      height="20vh" className="navbar-img"></img>
      <div className="login-form">
        <form onSubmit={handleSubmit}>
          <div className="form-floating">
            <input
              className="form-control"
              type="text"
              id="username"
              name="username"
              value={username}
              onChange={handleUsernameChange}
              placeholder="Username"
              required
            />
            <label htmlFor="username">Username</label>
          </div>
          <div className="form-floating">
            <input
              className="form-control"
              type={showPassword ? 'text' : 'password'}
              id="password"
              name="password"
              value={password}
              onChange={handlePasswordChange}
              placeholder="Password"
              required
            />
            <label htmlFor="password">Password</label>
          </div>
          <div style={{alignSelf:'center', position:'relative'}}>
            <button type="submit" className="btn btn-success">Login</button>
          </div>
          <div style={{alignSelf:'center', position:'relative'}}>
            <p className="mb-1">or</p>
          </div>
          <div style={{alignSelf:'center', position:'relative'}}>
            <button type="button" className="btn btn-success" onClick={handleSignup}>Signup</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
