const saveDataToLocalStorage = (key, data) => {
    localStorage.setItem(key, JSON.stringify(data));
};

const saveUserData = (name, id) => {
    const user = { name: name, id: id };
    saveDataToLocalStorage('user', user);
};

export default saveUserData;
