// Login.js
import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from './context/UserContext';
import './login.css';
import saveUserData from './local-data/UserSet';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons'

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();
  const show_icon = <FontAwesomeIcon icon={faEye} />;
  const hide_icon = <FontAwesomeIcon icon={faEyeSlash} />;

  // Set the value of username
  const { setUserId: setContextUserId } = useContext(UserContext);

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setContextUserId(username);
    const user = {
      name: 'jha',
      id: username,
    };
    saveUserData(user);

    // Clear the input fields after submission
    setUsername('');
    setPassword('');

    // Redirect to the profile page
    navigate('/profile');
  };

  const handleSignup = () => {
    navigate('/signup');
  };

  return (

  //   <form>
  //   <img class="mb-4" src="/docs/5.0/assets/brand/bootstrap-logo.svg" alt="" width="72" height="57">
  //   <h1 class="h3 mb-3 fw-normal">Please sign in</h1>

  //   <div class="form-floating">
  //     <input type="email" class="form-control" id="floatingInput" placeholder="name@example.com">
  //     <label for="floatingInput">Email address</label>
  //   </div>
  //   <div class="form-floating">
  //     <input type="password" class="form-control" id="floatingPassword" placeholder="Password">
  //     <label for="floatingPassword">Password</label>
  //   </div>

  //   <div class="checkbox mb-3">
  //     <label>
  //       <input type="checkbox" value="remember-me"> Remember me
  //     </label>
  //   </div>
  //   <button class="w-100 btn btn-lg btn-primary" type="submit">Sign in</button>
  //   <p class="mt-5 mb-3 text-muted">&copy; 2017–2021</p>
  // </form>

    <div className="container">
      <div className="login-form">
        <h2>Login</h2>
        <div className="form-floating">
          <input
            className='form-control'
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={handleUsernameChange}
            placeholder='Username'
            required
          />
          <label htmlFor="username">Username</label>
        </div>
        <div className="form-floating">
          <input
            className='form-control'
            type={showPassword ? 'text' : 'password'}
            id="password"
            name="password"
            value={password}
            onChange={handlePasswordChange}
            placeholder='Password'
            required
          />
          <label htmlFor="password">Password</label>
          <div className="button-container">
            <button
              type="button"
              className="password-toggle-button"
              onClick={togglePasswordVisibility}>
              {showPassword ? show_icon : hide_icon}
            </button>
          </div>
        </div>
        <div>
          <input type="submit" onClick={handleSubmit} value="Login" />
        </div>
        <button type="button" class="btn btn-success" onClick={handleSignup}>Signup</button>
      </div>
    </div>
  );
};

export default Login;
