import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from './context/UserContext';
import './login.css';
import saveUserData from './local-data/UserSet';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();
  const showIcon = <FontAwesomeIcon icon={faEye} />;
  const hideIcon = <FontAwesomeIcon icon={faEyeSlash} />;

  const { setUserId: setContextUserId } = useContext(UserContext);

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setContextUserId(email);
    const user = {
      name: 'jha',
      id: email,
    };
    saveUserData(user);

    setEmail('');
    setPassword('');

    navigate('/profile');
  };

  const handleSignup = () => {
    navigate('/signup');
  };

  return (
    <div className="container">
      <form className="login-form" onSubmit={handleSubmit}>
        <img
          className="mb-4"
          src="/docs/5.0/assets/brand/bootstrap-logo.svg"
          alt=""
          width="72"
          height="57"
        />
        <h1 className="h3 mb-3 fw-normal">Please sign in</h1>

        <div className="form-floating">
          <input
            type="email"
            className="form-control"
            id="floatingInput"
            placeholder="name@example.com"
            value={email}
            onChange={handleEmailChange}
            required
          />
          <label htmlFor="floatingInput">Email address</label>
        </div>
        <div className="form-floating">
          <input
            type={showPassword ? 'text' : 'password'}
            className="form-control"
            id="floatingPassword"
            placeholder="Password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
          <label htmlFor="floatingPassword">Password</label>
          <button
            type="button"
            className="password-toggle-button"
            onClick={togglePasswordVisibility}
          >
            {showPassword ? showIcon : hideIcon}
          </button>
        </div>

        <div className="checkbox mb-3">
          <label>
            <input type="checkbox" value="remember-me" /> Remember me
          </label>
        </div>
        <button className="w-100 btn btn-lg btn-primary" type="submit">
          Sign in
        </button>
        <p className="mt-5 mb-3 text-muted">&copy; 2017â€“2021</p>
      </form>
      <div className="button-container">
        <button type="button" onClick={handleSignup}>
          Signup
        </button>
      </div>
    </div>
  );
};

export default Login;
