import React, { useEffect, useState } from 'react';
import { Modal } from 'react-bootstrap';

const WarningPopup = () => {
    const [show, setShow] = useState(false);

    useEffect(() => {
      const handleBeforeUnload = (event) => {
        event.preventDefault();
        event.returnValue = ''; // For Chrome compatibility
        setShow(true);
      };

      window.addEventListener('beforeunload', handleBeforeUnload);

      return () => {
        window.removeEventListener('beforeunload', handleBeforeUnload);
      };
    }, []);

    const handleClose = () => {
      setShow(false);
      // You can perform additional actions here if needed
    };

    const handleLeave = () => {
      // You can perform additional actions here if needed
      window.location.href = 'https://example.com'; // Redirect to another page
    };

    return (
      <Modal size="lg" show={show} onHide={handleClose}>
        <Modal.Header>Warning</Modal.Header>
        <Modal.Body>Are you sure you want to leave?</Modal.Body>
        <Modal.Footer>
          <button onClick={handleClose}>Stay on Page</button>
          <button onClick={handleLeave}>Leave</button>
        </Modal.Footer>
      </Modal>
    );
  };

export default WarningPopup;
