import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { UserContext } from './context/UserContext';
import './login.css';
import saveUserData from './local-data/UserSet';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  // Set the value of username
  const { setUserId: setContextUserId } = useContext(UserContext);

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setContextUserId(username);
    const user = {
      name: 'jha',
      id: username,
    };
    saveUserData(user);

    // Clear the input fields after submission
    setUsername('');
    setPassword('');

    // Redirect to the profile page
    navigate('/profile');
  };

  const handleSignup = () => {
    navigate('/signup');
  };

  return (
    <div className="container p-5">
      <div className="login-form" style={{ alignItems: 'center', display: 'flex', flexDirection: 'column' }}>
        <img
          src="https://www.svnit.ac.in/conference/frsm2023/hit/svnit_logo.png"
          alt="logo"
          height="100vh"
          className="navbar-img"
        />
        <div className="mb-3"></div>
        <form onSubmit={handleSubmit}>
          <div className="form-floating">
            <input
              className="form-control"
              type="text"
              id="username"
              name="username"
              value={username}
              onChange={handleUsernameChange}
              placeholder=" "
              required
            />
            <label htmlFor="username" className="form-label text-center" style={{ width: '100%' }}>
              Username
            </label>
          </div>
          <div className="form-floating">
            <input
              className="form-control"
              type={showPassword ? 'text' : 'password'}
              id="password"
              name="password"
              value={password}
              onChange={handlePasswordChange}
              placeholder=" "
              required
            />
            <label htmlFor="password" className="form-label text-center" style={{ width: '100%' }}>
              Password
            </label>
          </div>
          <div style={{ alignSelf: 'center', position: 'relative' }}>
            <button type="submit" className="btn btn-success">
              Login
            </button>
          </div>
          <div style={{ alignSelf: 'center', position: 'relative' }}>
            <p className="mb-1">or</p>
          </div>
          <div style={{ alignSelf: 'center', position: 'relative' }}>
            <button type="button" className="btn btn-success" onClick={handleSignup}>
              Signup
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
